import { useState, useRef, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

export default function TextHeap() {
  const [hoveredId, setHoveredId] = useState(null);
  const [expandedId, setExpandedId] = useState(null);
  const [showButton, setShowButton] = useState(null);
  const [scrollY, setScrollY] = useState(0);
  const [scrollX, setScrollX] = useState(0);
  const navigate = useNavigate();

const leaveTimeoutRef = useRef(null);

const handleMouseLeave = () => {
  leaveTimeoutRef.current = setTimeout(() => {
    setHoveredId(null);
  }, 300);
};

const handleMouseEnter = (id) => {
  if (leaveTimeoutRef.current) {
    clearTimeout(leaveTimeoutRef.current);
  }
  setHoveredId(id);
};

  const styles = {
    big: { 
      className: "text-[0.875rem] font-bold text-black font-andada text-center",
      lineSpacing: '-5px',
      scaleFactor: 2.2
    },
    small: { 
      className: "text-[0.6rem] font-light text-black font-andada text-center",
      lineSpacing: '-3px',
      scaleFactor: 2.2
    },
    medium: { 
      className: "text-[0.7rem] font-light text-black font-andada text-center",
      lineSpacing: '-4px',
      scaleFactor: 2.2
    },
    xmedium: {
      className: "text-[0.7rem] font-normal text-black font-andada text-center",
      lineSpacing: '-4px',
      scaleFactor: 2.2
    }
  };

  const texts = [
    // Haufen 1 - Links oben
    { id: 1, text: ["Die Frau hat Jahrhundertelang als Lupe gedient,", "welche die magische und köstliche Fähigkeit besaß,", "den Mann doppelt so groß zu zeigen wie er von Natur aus ist", "– Virginia Woolf"], top: "8%", left: "10%", style: "big" },
    { id: 2, text: ["Wenn das Recht einer jungen Frau auf Unversehrtheit wirklich ernst genommen würde,", "gäbe es Selbstverteidigung für Frauen als Schulfach.", "Stattdessen lernen wir nach wie vor Anpassung", "– Luisa Francia"], top: "8%", left: "16%", style: "small" },
    { id: 3, text: [""], top: "11%", left: "17%", style: "medium" },
    { id: 4, text: ["Wüsstest du auch nur um die Hälfte der außergewöhnlichen unbesonnen Dinge,", "die ich tue,", "du würdest gewiss dem Gedanken zuneigen,", "dass irgendein Zauber auf mir liegt", "– Ada Lovelace"], top: "13%", left: "7%", style: "small", link: "/about" },
    { id: 5, text: [""], top: "14%", left: "11%", style: "big" },
    { id: 6, text: [""], top: "10.5%", left: "6%", style: "xmedium" },

    // Haufen 2 - Mitte oben
    { id: 7, text: ["Zu wissen, dass die Zeitgenossinnen meiner Großmutter", "nicht nur brave Ehefrauen, Mütter, Hausfrauen und Köchinnen waren,", "sondern vielmehr eine Generation potentieller Freiheitskämpferinnen,", "gibt ihrem Dasein eine neue Dimension und meinem Leben neue Kraft", "– Midge Mackenzie"], top: "14%", left: "45%", style: "medium" },
    { id: 8, text: [""], top: "12%", left: "46%", style: "big" },
    { id: 9, text: [""], top: "15%", left: "47%", style: "small" },
    { id: 10, text: [""], top: "13%", left: "45.5%", style: "small" },
    { id: 11, text: [""], top: "14%", left: "46.5%", style: "xmedium" },
    { id: 12, text: [""], top: "10%", left: "48%", style: "big" },
    { id: 13, text: [""], top: "13%", left: "44.5%", style: "small" },

    // Haufen 3 - Rechts oben
    { id: 14, text: ["Männer haben Angst, dass Frauen über sie lachen.", "Frauen haben Angst, dass Männer sie umbringen", "– Margaret Atwood"], top: "17%", left: "75%", style: "big" },
    { id: 15, text: [""], top: "22%", left: "76%", style: "xmedium" },
    { id: 16, text: ["Wir sehen die Dinge nicht,", "wie sie sind,", "wir sehen sie so,", "wie wir sind", "– Anais Nin"], top: "21%", left: "77%", style: "small" },
    { id: 17, text: [""], top: "23%", left: "75.5%", style: "small" },
    { id: 18, text: [""], top: "24%", left: "76.5%", style: "big" },
    { id: 19, text: ["Ich habe mich von Jugend an", "mit der Erforschung der Insekten beschäftigt.", "– Maria Sibylla Merian"], top: "20%", left: "78%", style: "medium", link: "/merian" },
    { id: 20, text: [""], top: "19%", left: "74.5%", style: "small" },

    // Haufen 4 - Links Mitte
    { id: 21, text: [""], top: "35%", left: "6%", style: "medium" },
    { id: 22, text: ["Es war wichtig,", "nicht rot zu werden,", "und das lernte ich schnell.", "Meine weiblichen Studienfreunde nahmen an,", "dass ich das Studienfach wechseln würde.", "Wenn ich nicht so sicher gewesen wäre,", "dass ich Radioastronomin werden wollte,", "hätte ich es vielleicht getan!", "– Jocelyn Bell Burnell"], top: "37%", left: "8%", style: "small" },
    { id: 23, text: [""], top: "36%", left: "7%", style: "small" },
    { id: 24, text: [""], top: "38%", left: "9%", style: "small" },
    { id: 25, text: ["Nach wie vor werden Frauen als eine Gefahr gesehen,", "die es einzudämmen gilt,", "als Wesen mit subversiver Kraft,", "die man in der Defensive halten und ausschließen muss.", "– Fatima Mernissi"], top: "39%", left: "7%", style: "xmedium" },
    { id: 26, text: [""], top: "35.5%", left: "6%", style: "big" },
    { id: 27, text: [""], top: "40%", left: "11%", style: "small" },
    { id: 28, text: ["Männer haben Angst, dass Frauen über sie lachen.", "Frauen haben Angst, dass Männer sie umbringen.", "– Margaret Atwood"], top: "34.5%", left: "12%", style: "medium" },

    // Haufen 5 - Zentrum
    { id: 29, text: [""], top: "45%", left: "48%", style: "big" },
    { id: 30, text: ["Es gibt keine Freiheit der Männer,", "wenn es nicht eine Freiheit der Frauen gibt.", "– Hedwig Dohm"], top: "47%", left: "49%", style: "medium" },
    { id: 31, text: ["Lasst uns von Frauen sprechen,", "die gut und wertvoll sind, und ihre großen Tugenden loben,", "denn sie verdienen Ehrung.", "– Christine de Pizan"], top: "46%", left: "50%", style: "small" },
    { id: 32, text: ["Lesen, schreiben, wissen;", "das sind die Schlüssel zur Emanzipation der Frau", "– Christine de Pizan"], top: "48%", left: "48.5%", style: "small" },
    { id: 33, text: [""], top: "49%", left: "49.5%", style: "big" },
    { id: 34, text: [""], top: "45.5%", left: "51%", style: "medium" },
    { id: 35, text: [""], top: "50%", left: "47.5%", style: "small" },
    { id: 36, text: ["Ich möchte, dass Frauen sich großen", "und schwierigen Aufgaben zuwenden.", "Sie sollen nicht dauernd an der Küste herumlungern,", "aus Angst davor in See zu stechen", "– Ethel Smyth"], top: "44.5%", left: "49.5%", style: "big" },
    { id: 37, text: [""], top: "51%", left: "50.5%", style: "medium" },
    { id: 38, text: [""], top: "43.5%", left: "48.5%", style: "small" },

    // Haufen 6 - Rechts Mitte
    { id: 39, text: ["Der Frau bleibt kein anderer Ausweg,", "als an ihrer Befreiung zu arbeiten.", "Diese Befreiung kann nur eine kollektive sein.", "– Simone de Beauvoir"], top: "41%", left: "85%", style: "medium" },
    { id: 40, text: [""], top: "42%", left: "79%", style: "big" },
    { id: 41, text: ["Das Patriarchat ist ein Richter, der uns bei Geburt schon verurteilt", "und unsere Strafe ist Gewalt, die du nicht siehst.", "Das Patriarchat ist ein Richter, der uns bei Geburt schon verurteilt", "und unsere Strafe ist die Gewalt, die du schon sehen wirst.", "Las Tesis"], top: "40%", left: "80%", style: "small" },
    { id: 42, text: [""], top: "43%", left: "78.5%", style: "small" },
    { id: 43, text: [""], top: "44%", left: "79.5%", style: "medium" },
    { id: 44, text: ["Ich weiß, ich könnte etwas werden,", "aber was soll man machen in Unterröcken?", "Die Heirat ist die einzige Karriere der Weiber,", "die Männer haben sechsunddreißig Chancen,", "das Weib nur eine.", "– Marie Bashkirtseff"], top: "42.5%", left: "74%", style: "big" },
    { id: 45, text: [""], top: "39.5%", left: "77.5%", style: "small" },

    // Haufen 7 - Links unten
    { id: 46, text: [""], top: "75%", left: "2%", style: "big" },
    { id: 47, text: [""], top: "77%", left: "4%", style: "medium" },
    { id: 48, text: [""], top: "76%", left: "1%", style: "small" },
    { id: 49, text: [""], top: "78%", left: "3%", style: "small" },
    { id: 50, text: [""], top: "79%", left: "6%", style: "big" },
    { id: 51, text: ["Selbst denken ist der höchste Mut.", "Wer wagt selbst zu denken,", "der wird auch selbst handeln", "–Bettina von Arnim"], top: "75.5%", left: "4%", style: "xmedium" },
    { id: 52, text: ["Gegen den Rückschritt gibt es nur ein Mittel:", "immer wieder von vorn anfangen", "– Teresa von Avila"], top: "80%", left: "2%", style: "small" },
    { id: 53, text: [""], top: "74.5%", left: "2%", style: "medium" },

    // Haufen 8 - Mitte unten
    { id: 54, text: [""], top: "70%", left: "50%", style: "medium" },
    { id: 55, text: ["Man will die sittliche Natur fördern,", "und man verdammt die Hälfte der Menschheit zur Dienerin der sinnlichen Natur.", "Was ist diese Häuslichkeit anderes als ein stetes Abmühen für die niedersten Bedürfnisse?", "– Louise Dittmar"], top: "72%", left: "51%", style: "big" },
    { id: 56, text: [""], top: "71%", left: "52%", style: "small" },
    { id: 57, text: [""], top: "73%", left: "50.5%", style: "small" },
    { id: 58, text: ["But an Englisch major who knew shorthand was somethiing else again.", "Everybody would want her.", "She would be in demand among all the up-and-coming young men", "and she would transcribe thriilling letter after thrilling leter.", "The trouble was, I hated the idea to serve men in any way.", "I wanted to dictate my own thrilling letters.", " – Sylvia Plath, The Bell Jar"], top: "74%", left: "51.5%", style: "medium" },
    { id: 59, text: [""], top: "70.5%", left: "53%", style: "big" },
    { id: 60, text: [""], top: "69.5%", left: "49.5%", style: "small" },

    // Haufen 9 - Rechts unten
    { id: 61, text: [""], top: "85%", left: "72%", style: "big" },
    { id: 62, text: [""], top: "87%", left: "73%", style: "medium" },
    { id: 63, text: ["I knew if I quit,", "nobody would ever believe that women", "had the capcity to run 26-plus miles.", "If I quit,", "everybody would say it was a publicity stunt.", "If I quit, it would set women's sports back,", "way back", "instead of forward.", "If I quit,", "I'd never run Boston.", "If I quit,", "Jock Semple and all those like him would win.", "– Kathrine Switzer"], top: "86%", left: "72%", style: "small" },
    { id: 64, text: [""], top: "88%", left: "72.5%", style: "small" },
    { id: 65, text: [""], top: "89%", left: "73.5%", style: "big" },
    { id: 66, text: [""], top: "85.5%", left: "75%", style: "medium" },
    { id: 67, text: ["Die Frauenarbeit abschaffen oder auch nur beschränken wollen,", "das läuft darauf hinaus", "die Frau zu dauernder ökonomischer Abhängigkeit,", "zur gesellschaftlichen Knechtung und Ächtung,", "zur Prostitution in- und außerhalb der Ehe zu verurteilen.", "– Clara Zetkin"], top: "90%", left: "71.5%", style: "small" },
    { id: 68, text: [""], top: "84.5%", left: "73.5%", style: "medium" },

    // Zusätzliche verstreute Texte
    { id: 69, text: [""], top: "25%", left: "35%", style: "big" },
    { id: 70, text: ["Wie der Arbieter vom Kapitalisten unterjocht wird,", "so die Frau vom Mann;", "und sie wird unterjocht bleiben, solnage sie nicht wirtschaftlich unabhängig dasteht.", "– Clara Zetkin"], top: "27%", left: "36%", style: "small" },
    { id: 71, text: ["Man kommt sich auf dem Gebiet der Frauenfrage immer wie ein Wiedekäuer vor.", "Das liegt an der Taktik der Gegner.", "– Hedwig Dohm"], top: "55%", left: "65%", style: "medium" },
    { id: 72, text: [""], top: "57%", left: "66%", style: "small" },
    { id: 73, text: [""], top: "30%", left: "60%", style: "big" },
    { id: 74, text: ["Mann bsit du im Stande gerecht zu sein?", "Es ist eine Frau,", "die dir diese Frage stellt;", "dieses Recht wenigstens kannst du ihr nicht nehmen.", "Sage mir,", "wer hat dir die souveräne Macht verliehen,", "mein Geschlecht zu unterdrücken?", "– Olympe de Gouges"], top: "32%", left: "61%", style: "medium" },
    { id: 75, text: [""], top: "60%", left: "20%", style: "small" },
    { id: 76, text: ["Weder Herkunft noch Geschlecht setzen dem Genie Grenzen.", "– Charlotte Bronte"], top: "62%", left: "21%", style: "big" },
    { id: 77, text: [""], top: "22%", left: "30%", style: "medium" },
    { id: 78, text: ["Ich zeifle,", "ob ich je heiraten werde.", "Ich glaube,", "dass eine Frau Karriere machen kann, wie ein Mann.", "– Mileva Maric"], top: "24%", left: "31%", style: "small" },
    { id: 79, text: [""], top: "82%", left: "45%", style: "big" },
    { id: 80, text: ["Schon eine Frau zu sein ist hier ein halbes Verbrechen", "– Lise Meitner"], top: "84%", left: "46%", style: "medium" },
  ];

const getTransform = (item) => {
  // Nur wenn expandedId gesetzt → Text wird groß
  if (expandedId === item.id) {
    const scaleFactor = styles[item.style].scaleFactor;
    return `translate(-50%, -50%) scale(${scaleFactor})`;
  }

  // Nur wenn hoveredId gesetzt → nahe Texte weichen aus
  if (hoveredId !== null && hoveredId !== item.id) {
    const leftPos = parseFloat(item.left);
    const topPos = parseFloat(item.top);

    const hoveredItem = texts.find(t => t.id === hoveredId);
    const hoveredLeft = parseFloat(hoveredItem.left);
    const hoveredTop = parseFloat(hoveredItem.top);

    const deltaX = leftPos - hoveredLeft;
    const deltaY = topPos - hoveredTop;

    const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);

    // Nahe Texte wegdrücken
    if (distance < 7) {
      const pushX = deltaX * 20;
      const pushY = deltaY * 20;
      return `translate(${pushX}px, ${pushY}px) scale(0.85)`;
    }
  }

  // Wenn expandedId gesetzt → alle anderen zum Rand
  if (expandedId !== null && expandedId !== item.id) {
    const leftPos = parseFloat(item.left);
    const topPos = parseFloat(item.top);

    const toLeft = leftPos < 50;
    const toTop = topPos < 50;
    const pushX = toLeft ? -leftPos * 3 : (100 - leftPos) * 3;
    const pushY = toTop ? -topPos * 3 : (100 - topPos) * 3;

    return `translate(${pushX}%, ${pushY}%) scale(0.7)`;
  }

  return 'translate(0, 0) scale(1)';
};
useEffect(() => {
  const handleScroll = () => {
    setScrollY(window.scrollY);
    setScrollX(window.scrollX);
  };

  window.addEventListener('scroll', handleScroll);
  return () => window.removeEventListener('scroll', handleScroll);
}, []);

  return (
    <div className="min-w-[120vw] min-h-[200vh] h-screen overflow-hidden bg-stone-50 relative">
       <div className="fixed top-6 left-8 z-[10000] pointer-events-none">
      <h1 className="text-4xl font-bold font-andada italic">
        Haupttitel
      </h1>
      <p className="text-lg font-andada -m-2 ml-16">
        Untertitel oder Beschreibung
      </p>
    </div>
        {/* Unsichtbarer Hintergrund zum Schließen - HIER EINFÜGEN */}
      {expandedId !== null && (
      <div 
       className="absolute inset-0 z-[9998]"
        onClick={() => {
         setExpandedId(null);
          setShowButton(null);  // ← Hinzufügen
      }}
      />
    )}
      {texts.map((item) => (
        <div
          key={item.id}
          className="absolute cursor-pointer"
          style={{ 
            top: item.top, 
            left: item.left,
            zIndex: expandedId === item.id ? 10000 : (hoveredId === item.id ? 9999 : 1),
            transform: getTransform(item),
        transition: hoveredId === item.id 
        ? 'transform 1.5s ease-out 1.2s, z-index 0s, top 1.5s ease-out 1.2s, left 1.5s ease-out 1.2s'
        : hoveredId === null
        ? 'transform 0.1.2s ease-out 0.8s, z-index 0s 0.8s'  // Zurück: keine Verzögerung
        : 'transform 0.8s ease-out 0.8s, z-index 0s 0.3s',  // Zur Seite: mit Verzögerung,
            pointerEvents: hoveredId !== null && hoveredId !== item.id ? 'none' : 'auto',
            ...(expandedId === item.id && {
              top: `${scrollY + window.innerHeight / 2}px`,
              left: `${scrollX + window.innerWidth / 2}px`,
            })
          }}
        onMouseEnter={() => handleMouseEnter(item.id)}
        onMouseLeave={handleMouseLeave}
        onClick={() => {
        if (expandedId !== item.id) {
          setExpandedId(item.id);
          setTimeout(() => {
          setShowButton(item.id);
          }, 2700);
          }
        }}
        >
          <div 
            className={`${styles[item.style].className} tracking-wide`}
          >
            {Array.isArray(item.text) 
              ? item.text.map((line, i) => (
                  <div key={i} style={{ marginTop: i > 0 ? styles[item.style].lineSpacing : '0' }}>
                    {line}
                  </div>
                ))
              : item.text
            }
          </div>
<div>
  <div className={`${styles[item.style].className} tracking-wide`}>
    {/* Dein Text */}
  </div>

  <div className="flex justify-end">
    {showButton === item.id && item.link && (
  <button
    onClick={(e) => {
      e.stopPropagation();
      navigate(item.link);
    }}
    style={{
      position: 'absolute',
      bottom: '-6px',  // Unter dem Text
      right: '110px'
    }}
    className="px-2 py-1 text-sm text-white font-typewriter transition-colors z-[9999]"
  >
    <svg width="200" height="6" viewBox="0 0 200 12">
      {/* Linie */}
      <line x1="0" y1="6" x2="192" y2="6" stroke="black" strokeWidth="2"/>
      {/* Pfeilspitze */}
      <polygon points="200,6 190,0 190,12" fill="black"/>
    </svg>
  </button>
    )}
  </div>
</div>
        </div>
      ))}
    </div>
  );
}
