import { Link } from 'react-router-dom'
import { useState } from 'react'

function ImageWithHover({ section, index }) {
  const [isHovered, setIsHovered] = useState(false);
  const [timeoutId, setTimeoutId] = useState(null);

  const handleMouseEnter = () => {
    if (timeoutId) {
      clearTimeout(timeoutId);
      setTimeoutId(null);
    }
    setIsHovered(true);
  };

  const handleMouseLeave = () => {
    const id = setTimeout(() => {
      setIsHovered(false);
    }, 10000);
    setTimeoutId(id);
  };

  return (
    <div 
      className="relative w-full h-auto"
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      <img 
        src={section.srcBW || section.src}
        alt={section.alt || `Bild ${index}`}
        className={`w-full h-auto transition-opacity duration-[2000ms] ease-in-out ${section.className || ''}`}
        style={{
          ...(section.style || {}),
          ...(section.rotate && { transform: `rotate(${section.rotate})` }),
          opacity: isHovered ? 0 : 1
        }}
      />

      {section.srcColor && (
        <img 
          src={section.srcColor}
          alt={section.alt || `Bild ${index}`}
          className={`w-full h-auto absolute top-0 left-0 transition-opacity duration-[2000ms] ease-in-out ${section.className || ''}`}
          style={{
            ...(section.style || {}),
            ...(section.rotate && { transform: `rotate(${section.rotate})` }),
            pointerEvents: 'none',
            opacity: isHovered ? 1 : 0
          }}
        />
      )}
    </div>
  );
}

export default function InfoPageLayout({
  title, 
  subtitle = '',
  backgroundColor = 'bg-white',
  backButtonColor = '#991b1b',
  headerImage,
  headerImageSize = 'cover',
  headerImagePosition = 'center',
  headerImageOpacity = '1',
  headerImageRotate = '0deg',
  headerImageOffset = { top: '0', left: '0', right: '0', bottom: '0' },
  headerImageWidth = '100%',
  headerImageHeight = '100%',
  headerTexts = [],
  backgroundImages = [],
  sections = [],
  showBackButton = true
}) {
  return (
    <div className={`min-h-screen ${backgroundColor}`} style={{ overflowX: 'clip' }}>
      {/* Header Bereich - Responsiv */}
      <div className="relative h-[60vh] md:h-screen">
        {showBackButton && (
          <Link 
            to="/" 
            className="fixed top-4 left-4 md:top-6 md:left-8 z-50 hover:opacity-60 transition-opacity"
          >
            <svg className="w-16 h-16 md:w-24 md:h-24 lg:w-[120px] lg:h-[120px]" viewBox="0 0 120 120">
              <defs>
                <radialGradient id={`fadeGradient-${backButtonColor.replace('#', '')}`}>
                  <stop offset="0%" stopColor={backButtonColor} stopOpacity="1" />
                  <stop offset="15%" stopColor={backButtonColor} stopOpacity="0.95" />
                  <stop offset="30%" stopColor={backButtonColor} stopOpacity="0.8" />
                  <stop offset="45%" stopColor={backButtonColor} stopOpacity="0.5" />
                  <stop offset="60%" stopColor={backButtonColor} stopOpacity="0.2" />
                  <stop offset="75%" stopColor={backButtonColor} stopOpacity="0.05" />
                  <stop offset="90%" stopColor={backButtonColor} stopOpacity="0" />
                </radialGradient>
              </defs>
              <circle cx="60" cy="60" r="55" fill={`url(#fadeGradient-${backButtonColor.replace('#', '')})`} />
            </svg>
          </Link>
        )}

        {/* Header Hintergrundbild - kann über Ränder hinausgehen */}
        {headerImage && (
          <div className="absolute inset-0 overflow-visible">
            <img 
              src={headerImage} 
              alt="Header" 
              className="absolute object-cover"
              style={{
                top: headerImageOffset.top || '0',
                left: headerImageOffset.left || '0', 
                right: headerImageOffset.right || '0',  
                bottom: headerImageOffset.bottom || '0',
                width: headerImageWidth,
                height: headerImageHeight,
                objectFit: headerImageSize,
                objectPosition: headerImagePosition,
                opacity: headerImageOpacity,
                transform: `rotate(${headerImageRotate})`
              }}
            />
          </div>
        )}

        {/* Titel - Fixed Position auf Desktop */}
        <h1 className={`
          absolute 
          top-8 left-4 
          md:top-32 md:left-60
          text-3xl md:text-5xl lg:text-6xl
          font-bold font-andada text-black z-20 
          max-w-xs md:max-w-2xl bg-white/95 md:bg-white
          p-2 md:pb-2 md:pt-0 md:pl-0
        `}>
          {title}
        </h1>

        {/* Untertitel - Fixed Position auf Desktop, direkt unter Titel */}
        {subtitle && (
          <div className={`
            absolute 
            top-32 left-4 
            md:top-[168px] md:left-60
            max-w-xs md:max-w-xl
            z-10
          `}>
            <p className="text-xs md:text-sm leading-relaxed font-andada text-black bg-white/95 md:bg-white p-2 md:pt-0 md:pl-0">
              {subtitle}
            </p>
          </div>
        )}
      </div>

      {/* Header Texts - Nur auf Desktop */}
      <div className="hidden md:block">
        {headerTexts.map((text, i) => (
          <div 
            key={i}
            className={`absolute z-10 ${text.position.top || ''} ${text.position.bottom || ''} ${text.position.left || ''} ${text.position.right || ''} ${text.width || 'max-w-xl'}`}
          >
            {Array.isArray(text.content) 
              ? text.content.map((line, j) => (
                  <p 
                    key={j}
                    className={`font-andada ${text.className || 'text-lg'} mb-2`}
                    style={{ 
                      lineHeight: text.lineHeight,
                      marginLeft: line.indent ? `${line.indent}rem` : 0,
                      marginTop: line.spacing ? `${line.spacing}rem` : 0
                    }}
                    dangerouslySetInnerHTML={{ __html: line.content || line }}
                  />
                ))
              : (
                  <p 
                    className={`font-andada ${text.className || 'text-lg'}`}
                    style={{ lineHeight: text.lineHeight }}
                    dangerouslySetInnerHTML={{ __html: text.content }} 
                  />
                )
            }
          </div>
        ))}
      </div>

      {/* Scrollbare Sections */}
      <div className="relative">
        {/* Background Images - Nur Desktop */}
        <div className="hidden md:block">
          {backgroundImages.map((bg, i) => (
            <div
              key={`bg-${i}`}
              className="absolute z-0"
              style={{
                backgroundImage: `url('${bg.image}')`,
                top: bg.top,
                left: bg.left,
                width: bg.width,
                height: bg.height,
                backgroundSize: bg.size || 'contain',
                backgroundRepeat: 'no-repeat',
                backgroundPosition: bg.position || 'center',
                pointerEvents: 'none'
              }}
            />
          ))}
        </div>

        {sections.map((section, index) => {
          // TEXT SECTION
          if (section.type === 'text') {
            return (
              <div 
                key={index}
                className={`
                  py-8 px-4 
                  md:py-20 md:px-12 
                  ${section.align === 'right' ? 'md:ml-auto' : ''} 
                  ${section.align === 'center' ? 'md:mx-auto' : ''} 
                  ${section.className || ''}
                `}
                style={{ 
                  maxWidth: section.width || '100%',
                }}
              >
                {/* Desktop: Mit Indents und Spacing */}
                <div className="hidden md:block"
                  style={{
                    lineHeight: section.lineHeight || undefined,
                    marginTop: section.marginTop || undefined,
                    marginRight: section.marginRight || undefined,
                    marginLeft: section.marginLeft || undefined,
                    maxWidth: section.width || '100%',  // ← NEU: Width auch auf Desktop
                    width: section.width || '100%'      // ← NEU
                  }}
                >
                  {section.heading && (
                    <h2 className={`${section.headingSize || 'text-3xl'} font-bold mb-6 font-andada`}>
                      {section.heading}
                    </h2>
                  )}

                  {section.paragraphs.map((para, i) => {
                    const content = typeof para === 'string' ? para : para.content;
                    const indent = typeof para === 'object' ? para.indent : 0;
                    const spacing = typeof para === 'object' ? para.spacing : 0;
                    const paraClassName = typeof para === 'object' ? para.className : '';
                    const paraSize = typeof para === 'object' ? para.size : undefined;
                    const paraLineHeight = typeof para === 'object' ? para.lineHeight : undefined;

                    return (
                      <p 
                        key={i}
                        className={`text-lg font-andada text-black mb-4 ${paraClassName} ${section.className || ''}`}
                        style={{ 
                          marginLeft: indent ? `${indent}rem` : 0,
                          marginTop: spacing ? `${spacing}rem` : 0,
                          fontSize: paraSize,
                          lineHeight: paraLineHeight || section.lineHeight,
                          hyphens: 'auto',
                          WebkitHyphens: 'auto',
                          MsHyphens: 'auto'
                        }}
                        lang="de"
                        dangerouslySetInnerHTML={{ __html: content }}
                      />
                    );
                  })}
                </div>

                {/* Mobile: Vereinfacht ohne Indents */}
                <div className="md:hidden">
                  {section.heading && (
                    <h2 className="text-2xl font-bold mb-4 font-andada">
                      {section.heading}
                    </h2>
                  )}

                  {section.paragraphs.map((para, i) => {
                    const content = typeof para === 'string' ? para : para.content;

                    return (
                      <p 
                        key={i}
                        className="text-base font-andada text-black mb-3"
                        style={{ 
                          hyphens: 'auto',
                          WebkitHyphens: 'auto',
                          MsHyphens: 'auto'
                        }}
                        lang="de"
                        dangerouslySetInnerHTML={{ __html: content }}
                      />
                    );
                  })}
                </div>
              </div>
            );
          }

          // IMAGE SECTION
          if (section.type === 'image') {
            // Absolute Positionierung - Nur Desktop
            if (section.position === 'absolute') {
              return (
                <div key={index}>
                  {/* Desktop: Absolute */}
                  <div className="hidden md:block absolute z-10"
                    style={{
                      position: 'absolute',
                      zIndex: section.zIndex || 10,
                      top: section.top,
                      bottom: section.bottom,
                      left: section.left,
                      right: section.right,
                      marginTop: section.marginTop || undefined
                    }}
                  >
                    <div className={`flex items-start gap-4 ${section.captionLeft ? 'flex-row-reverse' : ''}`}>
                      <img 
                        src={section.src}
                        alt={section.alt || `Bild ${index}`}
                        className={`${section.size || 'w-2/3'} ${section.className || ''}`}
                        style={section.style || {}}
                      />
                      {section.caption && (
                        <div className="text-sm text-black font-andada italic max-w-xs">
                          {Array.isArray(section.caption) 
                            ? section.caption.map((line, i) => (
                                <p key={i} className="mb-1">{line}</p>
                              ))
                            : <p>{section.caption}</p>
                          }
                        </div>
                      )}
                    </div>
                  </div>

                  {/* Mobile: Normal Flow */}
                  <div className="md:hidden py-4 px-4">
                    <img 
                      src={section.src}
                      alt={section.alt || `Bild ${index}`}
                      className="w-full h-auto"
                    />
                    {section.caption && (
                      <div className="text-xs text-black font-andada italic mt-2">
                        {Array.isArray(section.caption) 
                          ? section.caption.map((line, i) => (
                              <p key={i} className="mb-1">{line}</p>
                            ))
                          : <p>{section.caption}</p>
                        }
                      </div>
                    )}
                  </div>
                </div>
              );
            }

            // Relative Positionierung - mit Desktop/Mobile Split
            return (
              <div key={index}>
                {/* Desktop: Original Layout mit Überlappungen */}
                <div 
                  className="hidden md:block py-20 px-12 relative"
                  style={{ 
                    marginTop: section.marginTop,
                    marginLeft: section.marginLeft,
                    marginRight: section.marginRight,
                    zIndex: section.zIndex || 1,
                    pointerEvents: 'none',
                    touchAction: 'auto'
                  }}
                >
                  <div className={`
                    ${section.align === 'right' ? 'ml-auto' : ''} 
                    ${section.captionAbsolute ? 'relative' : ''}
                  `}
                    style={{ 
                      pointerEvents: 'none',
                      width: 'fit-content',
                      marginLeft: section.align === 'right' ? 'auto' : '0'
                    }}
                  >
                    <div className={`
                      flex items-start w-fit
                      ${section.captionPosition === 'below' ? 'flex-col' : ''} 
                      ${section.captionPosition === 'above' ? 'flex-col-reverse' : ''} 
                      ${section.captionPosition === 'left' ? 'flex-row-reverse' : ''} 
                      ${section.captionAlign === 'bottom' ? 'items-end' : 'items-start'}
                      ${section.captionGap || 'gap-4'}
                    `}
                      style={{ pointerEvents: 'none' }}
                    >
                      <div className="relative" style={{ pointerEvents: 'auto' }}>
                        {section.srcColor ? (
                          <div className={section.size || ''} style={{ width: section.customWidth || 'auto' }}>
                            <ImageWithHover section={section} index={index} />
                          </div>
                        ) : (
                          <img 
                            src={section.src}
                            alt={section.alt || `Bild ${index}`}
                            className={`${section.size || 'w-2/3'} ${section.className || ''}`}
                            style={{
                              ...(section.style || {}),
                              width: section.customWidth || undefined,
                              ...(section.rotate && { transform: `rotate(${section.rotate})` })
                            }}
                          />
                        )}
                      </div>

                      {section.caption && (
                        <div 
                          className={`
                            text-xs text-black font-andada tracking-wide italic 
                            ${section.captionAbsolute ? '' : (section.captionWidth || 'max-w-xl')} 
                            ${section.captionAbsolute ? 'absolute' : 'relative'}
                          `}
                          style={{ 
                            ...(section.captionAbsolute && {
                              top: section.captionTop || 'auto',
                              left: section.captionLeft || 'auto',
                              right: section.captionRight || 'auto',
                              bottom: section.captionBottom || 'auto'
                            }),
                            transform: section.captionRotate ? `rotate(${section.captionRotate})` : undefined,
                            lineHeight: section.captionLineHeight || undefined,
                            pointerEvents: 'auto'
                          }}
                        >
                          {Array.isArray(section.caption) 
                            ? section.caption.map((line, i) => (
                                <p key={i} className="mb-1" dangerouslySetInnerHTML={{ __html: line }} />
                              ))
                            : <p dangerouslySetInnerHTML={{ __html: section.caption }} />
                          }
                        </div>
                      )}
                    </div>
                  </div>
                </div>

                {/* Mobile: Vereinfachtes Layout */}
                <div className="md:hidden py-4 px-4">
                  <div className="w-full max-w-full overflow-hidden">
                    {section.srcColor ? (
                      <ImageWithHover section={{...section, rotate: undefined}} index={index} />
                    ) : (
                      <img 
                        src={section.src}
                        alt={section.alt || `Bild ${index}`}
                        className="w-full h-auto"
                      />
                    )}
                  </div>

                  {section.caption && (
                    <div className="text-xs text-black font-andada italic mt-2">
                      {Array.isArray(section.caption) 
                        ? section.caption.map((line, i) => (
                            <p key={i} className="mb-1" dangerouslySetInnerHTML={{ __html: line }} />
                          ))
                        : <p dangerouslySetInnerHTML={{ __html: section.caption }} />
                      }
                    </div>
                  )}
                </div>
              </div>
            );
          }

          // IMAGE TEXT SECTION
          if (section.type === 'imageText') {
            return (
              <div 
                key={index}
                className={`
                  py-8 px-4 
                  md:py-20 md:px-12 
                  grid grid-cols-1 
                  md:grid-cols-${section.imageRight ? '[1fr,1.5fr]' : '[1.5fr,1fr]'} 
                  gap-6 md:gap-12 
                  items-center
                `}
              >
                {!section.imageRight && (
                  <img 
                    src={section.image.src}
                    alt={section.image.alt || 'Bild'}
                    className={`w-full ${section.image.className || ''}`}
                  />
                )}
                <div>
                  {section.heading && (
                    <h2 className={`text-2xl md:${section.headingSize || 'text-3xl'} font-bold mb-4 md:mb-6 font-andada`}>
                      {section.heading}
                    </h2>
                  )}
                  {section.paragraphs.map((para, i) => (
                    <p 
                      key={i}
                      className="text-base md:text-lg leading-relaxed font-andada mb-4"
                      style={{
                        hyphens: 'auto',
                        WebkitHyphens: 'auto',
                        MsHyphens: 'auto'
                      }}
                      lang="de"
                    >
                      {para.content || para}
                    </p>
                  ))}
                </div>
                {section.imageRight && (
                  <img 
                    src={section.image.src}
                    alt={section.image.alt || 'Bild'}
                    className={`w-full ${section.image.className || ''}`}
                  />
                )}
              </div>
            );
          }

          return null;
        })}
      </div>
    </div>
  );
}
