                  return (
                    <p 
                      key={i}
                      className={`text-base md:text-lg font-andada text-black mb-3 md:mb-4 ${paraClassName} ${section.className || ''}`}
                      style={{ 
                        // Mobile: kein Indent
                        marginLeft: window.innerWidth >= 768 && indent ? `${indent}rem` : 0,
                        marginTop: window.innerWidth >= 768 && spacing ? `${spacing}rem` : 0,
                        fontSize: paraSize,
                        lineHeight: paraLineHeight || section.lineHeight,
                        hyphens: 'auto',
                        WebkitHyphens: 'auto',
                        MsHyphens: 'auto'
                      }}
                      lang="de"
                      dangerouslySetInnerHTML={{ __html: content }}
                    />
                  );
                })}
              </div>
            );
