import { Link } from 'react-router-dom'
export default function InfoPageLayout({ 
  title, 
  subtitle = '',
  titlePosition = { top: 'top-12', left: 'left-[140px]' },
  backgroundColor = 'bg-white',
  subtitlePosition = { top: 'top-28', right: 'left-[200px]', width: 'max-w-2xl' },
  titleSize = 'text-6xl',
  headerImage,
  headerImageSize = 'cover',
  headerImagePosition = 'center',
  headerImageOpacity = '1',
  headerImageRotate = '0deg',
  headerImageOffset = { top: '0', left: '0', right: '0', bottom: '0' },
  headerImageWidth = '100%',
  headerImageHeight = '100%',
  headerTexts = [],
  backgroundImages = [],
  sections = [],
  showBackButton = true
}) {
  return (
    <div className={`min-h-screen ${backgroundColor} overflow-x-hidden`}>
      {/* Header Bereich */}
      <div className="relative h-screen">
              {showBackButton && (
          <Link 
            to="/" 
              className="fixed top-8 left-8 z-50 hover:opacity-60 transition-opacity"
          >
        <svg width="120" height="120" viewBox="0 0 120 120">
          <defs>
              <radialGradient id="fadeGradientWhite">
              <stop offset="0%" stopColor="#991b1b" stopOpacity="0.9" />
              <stop offset="50%" stopColor="#991b1b" stopOpacity="0.6" />
              <stop offset="100%" stopColor="#991b1b" stopOpacity="0" />
              </radialGradient>
          </defs>
            <circle cx="60" cy="60" r="55" fill="url(#fadeGradientWhite)" />
        </svg>
  </Link>
      )}
        {/* Header Hinterrgrundbild (optional) */}
        {headerImage && (
          <img 
            src={headerImage} 
            alt="Header" 
            className="absolute top-0 left-0 w-full h-full object-cover"
            style={{
              top: headerImageOffset.top || '0',
              left: headerImageOffset.left || '0', 
              right: headerImageOffset.right || '0',  
              bottom: headerImageOffset.bottom || '0',
              width: headerImageWidth,
              height: headerImageHeight,
              objectFit: headerImageSize,
              objectPosition: headerImagePosition,
              opacity: headerImageOpacity,
              transform: `rotate(${headerImageRotate})`
            }}
          />
        )}

        {/* Titel links */}
        <h1 className={`absolute ${titlePosition.top} ${titlePosition.left} ${titleSize} font-bold font-andada text-black z-10 max-w-2xl`}>
          {title}
        </h1>

        {/* Untertitel/Beschreibung rechts */}
        {subtitle && (
         <div className={`absolute ${subtitlePosition.top} ${subtitlePosition.right} ${subtitlePosition.width || 'max-w-md'} z-10`}>
            <p className="text-sm leading-relaxed font-andada text-black">
              {subtitle}
            </p>
          </div>
        )}
      </div>
  {headerTexts.map((text, i) => (
  <div 
    key={i}
    className={`absolute z-10 ${text.position.top || ''} ${text.position.bottom || ''} ${text.position.left || ''} ${text.position.right || ''} ${text.width || 'max-w-xl'}`}
  >
    {Array.isArray(text.content) 
      ? text.content.map((line, j) => (
          <p 
            key={j}
            className={`font-andada ${text.className || 'text-lg'} mb-2`}
            style={{ 
              lineHeight: text.lineHeight,
              marginLeft: line.indent ? `${line.indent}rem` : 0,
              marginTop: line.spacing ? `${line.spacing}rem` : 0
            }}
              dangerouslySetInnerHTML={{ __html: line.content || line }}
          />

        ))
      : (
          <p 
            className={`font-andada ${text.className || 'text-lg'}`}
            style={{ lineHeight: text.lineHeight }}
            dangerouslySetInnerHTML={{ __html: text.content }} 
          />

        )
    }
  </div>
))}

      {/* Scrollbare Sections */}
      <div className="relative">
        {backgroundImages.map((bg, i) => (
    <div
      key={`bg-${i}`}
      className="absolute z-0"
      style={{
        backgroundImage: `url('${bg.image}')`,
        top: bg.top,
        left: bg.left,
        width: bg.width,
        height: bg.height,
        backgroundSize: bg.size || 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: bg.position || 'center',
        pointerEvents: 'none'
      }}
    />
  ))}
        {sections.map((section, index) => {
if (section.type === 'text') {
  return (
    <div 
      key={index}
      className={`py-20 px-12 ${section.align === 'right' ? 'ml-auto' : ''} ${section.align === 'center' ? 'mx-auto' : ''} ${section.className || ''}`}
      style={{ 
        maxWidth: section.width || '70%',
        lineHeight: section.lineHeight || undefined,
        marginTop: section.marginTop || undefined,
        marginRight: section.marginRight || undefined,
        marginLeft: section.marginLeft || undefined
      }}
    >
      {section.heading && (
        <h2 className={`${section.headingSize || 'text-3xl'} font-bold mb-6 font-andada`}>
          {section.heading}
        </h2>
      )}
      {section.paragraphs.map((para, i) => {
        // Support für String, Objekt mit content, oder Objekt mit content + indent + spacing
        const content = typeof para === 'string' ? para : para.content;
        const indent = typeof para === 'object' ? para.indent : 0;
        const spacing = typeof para === 'object' ? para.spacing : 0;
        const paraClassName = typeof para === 'object' ? para.className : '';
        const paraSize = typeof para === 'object' ? para.size : undefined;
        const paraLineHeight = typeof para === 'object' ? para.lineHeight : undefined;

        return (
      <p 
        key={i}
        className={`text-lg font-andada text-black mb-4 ${paraClassName} ${section.className || ''}`}
        style={{ 
          marginLeft: indent ? `${indent}rem` : 0,
          marginTop: spacing ? `${spacing}rem` : 0,
          fontSize: paraSize,
          lineHeight: paraLineHeight || section.lineHeight
       }}
          dangerouslySetInnerHTML={{ __html: content }}
      />
        );
      })}
    </div>
  );
}

if (section.type === 'image') {
    // Absolute Positionierung
  if (section.position === 'absolute') {
    return (
      <div
        key={index}
        className="absolute z-10"
      style={{
        position: 'absolute',
        zIndex: section.zIndex || 10,
        top: section.top,
        bottom: section.bottom,
        left: section.left,
        right: section.right,
        marginTop: section.marginTop || undefined
  }}
      >
        <div className={`flex items-start gap-4 ${section.captionLeft ? 'flex-row-reverse' : ''}`}>
          <img 
            src={section.src}
            alt={section.alt || `Bild ${index}`}
            className={`${section.size || 'w-2/3'} ${section.className || ''}`}
            style={section.style || {}}
          />
          {section.caption && (
            <div className="text-sm text-black font-andada italic max-w-xs">
              {Array.isArray(section.caption) 
                ? section.caption.map((line, i) => (
                    <p key={i} className="mb-1">{line}</p>
                  ))
                : <p>{section.caption}</p>
              }
            </div>
          )}
        </div>
      </div>
    );
  }
  return (
  <div 
    key={index}
    className="py-20 px-12 flex"
    style={{ 
      marginTop: section.marginTop,
      marginLeft: section.marginLeft,
      marginRight: section.marginRight,
      zIndex: section.zIndex || 'auto'
    }}
  >
    <div className={`${section.align === 'right' ? 'ml-auto' : ''} ${section.captionAbsolute ? 'relative' : ''}`}>
      <div className={`flex items-start gap-4 w-fit ...
    ${section.captionPosition === 'below' ? 'flex-col' : ''} 
    ${section.captionPosition === 'above' ? 'flex-col-reverse' : ''} 
    ${section.captionPosition === 'left' ? 'flex-row-reverse' : ''} 
    ${section.captionPosition === 'right' || !section.captionPosition ? 'flex-row' : ''}
    ${section.captionAlign === 'bottom' ? 'items-end' : 'items-start'}
    ${section.captionPosition === 'below' || section.captionPosition === 'above' ? '' : (section.captionGap || 'gap-4')}`}>
        <img 
          src={section.src}
          alt={section.alt || `Bild ${index}`}
          className={`${section.size || 'w-2/3'} ${section.className || ''}`}
          style={section.style || {}}
        />
       {section.caption && (
  <div 
    className={`text-xs text-black font-inter italic ${section.captionAbsolute ? '' : (section.captionWidth || 'max-w-lg')} ${section.captionAbsolute ? 'absolute' : ''}`}
    style={{ 
      ...(section.captionAbsolute && {
        top: section.captionTop || 'auto',
        left: section.captionLeft || 'auto',
        right: section.captionRight || 'auto',
        bottom: section.captionBottom || 'auto'
      }),
      transform: section.captionRotate ? `rotate(${section.captionRotate})` : undefined,
      lineHeight: section.captionLineHeight || undefined
    }}
  >
    {Array.isArray(section.caption) 
      ? section.caption.map((line, i) => (
          <p key={i} className="mb-1" dangerouslySetInnerHTML={{ __html: line }} />
        ))
      : <p dangerouslySetInnerHTML={{ __html: section.caption }} />
    }
  </div>
)}
      </div>
    </div>
  </div>
);
}

          if (section.type === 'imageText') {
            return (
              <div 
                key={index}
                className={`py-20 px-12 grid ${section.imageRight ? 'grid-cols-[1fr,1.5fr]' : 'grid-cols-[1.5fr,1fr]'} gap-12 items-center`}
              >
                {!section.imageRight && (
                  <img 
                    src={section.image.src}
                    alt={section.image.alt || 'Bild'}
                    className={`w-full ${section.image.className || ''}`}
                  />
                )}
                <div>
                  {section.heading && (
                    <h2 className={`${section.headingSize || 'text-3xl'} font-bold mb-6 font-andada`}>
                      {section.heading}
                    </h2>
                  )}
                  {section.paragraphs.map((para, i) => (
                    <p 
                      key={i}
                      className="text-lg leading-relaxed font-andada mb-4"
                    >
                      {para.content || para}
                    </p>
                  ))}
                </div>
                {section.imageRight && (
                  <img 
                    src={section.image.src}
                    alt={section.image.alt || 'Bild'}
                    className={`w-full ${section.image.className || ''}`}
                  />
                )}
              </div>
            );
          }
          return null;
        })}
      </div>
    </div>
  );
}
