import InfoPageLayout from '../components/InfoPageLayout'

export default function About() {
  return (
    <InfoPageLayout
      title="Ada Lovelace"
      subtitle="(1818–1852) Mathematikerin, die einen wichtigen Beitrag zur frühen Computertechnik lieferte."
      headerImage="/images/Lovelace_Algorithmus_Auszug_ausg.png"
      backgroundImages={[
    {
      image: '/images/Lovelace-Algo-ausg-02.png',
      top: '-8%',
      left: '2%',
      width: '80%',
      height: '900px',
      size: '120% auto',
      opacity: 0.5
    }

  ]}
  headerTexts={[
    {
      content: 'Zusätzlicher Text oben',
      position: { top: 'top-32', left: 'left-20' },
      className: 'text-2xl italic text-white',
      width: 'max-w-lg',
      lineHeight: '1.8'
    },
    {
      content: 'Noch ein Text unten rechts',
      position: { top: 'bottom-20', right: 'right-[100px]' },
      className: 'text-sm text-black'
    }
  ]}
      sections={[
                // Nur Text (rechts oben)
        {
          type: 'text',
          align: 'right',
          top: '-8%',
          heading: '',
          className: 'italic',
          lineHeight: '0.5',
          paragraphs: [
            '“Die Maschine könnte auch andere Dinge außer Zahlen bearbeiten,',
            'wenn sich Objekte finden ließen,',
            'deren Beziehungen durch die abstrakte Wissenschaft',
            'der Operationen ausgedrückt werden können',
            'und die sich für Befehle und den Mechanismus des Geräts eignen.“',
          ]
        },

        // Nur Text (links)
        {
          type: 'text',
          align: 'left',
          heading: '',
          className: 'italic',
          lineHeight: '0.5',
          paragraphs: [
            '“Die Maschine könnte auch andere Dinge außer Zahlen bearbeiten,',
            'wenn sich Objekte finden ließen,',
            'deren Beziehungen durch die abstrakte Wissenschaft',
            'der Operationen ausgedrückt werden können',
            'und die sich für Befehle und den Mechanismus des Geräts eignen.“',
          ]
        },

        // Nur Bild (zentriert)
        {
          type: 'image',
          src: '/images/Ada-Lovelace.jpg',
          alt: 'Ada Lovelace Portrait',
          align: 'right',
          size: 'w-1/6',
          caption: [
            'Ada Lovelace, 1840,',
            'gemalt von Margaret Sarah Carpenter,',
            'einer der erfolgreichsten',
            'Künstlerinnen ihrer Zeit',
            'in Großbritannien'
          ]
        },

        // Text rechts
        {
          type: 'text',
          align: 'right',
          width: '40%',
          paragraphs: [
            'Ein weiterer Abschnitt...',
            'Mehr Text hier...'
          ]
        },

        // Bild + Text nebeneinander (Bild links)
        {
          type: 'imageText',
          imageRight: false,
          image: {
            src: '/images/algorithm.jpg',
            alt: 'Algorithmus'
          },
          heading: 'Der erste Algorithmus',
          paragraphs: [
            'Ada schrieb 1843...',
            'Dies war der erste...'
          ]
        },

        // Bild + Text nebeneinander (Bild rechts)
        {
          type: 'imageText',
          imageRight: true,
          image: {
            src: '/images/machine.jpg',
            alt: 'Analytical Engine'
          },
          heading: 'Die Analytical Engine',
          paragraphs: [
            'Charles Babbage entwickelte...',
            'Ada erkannte das Potenzial...'
          ]
        }
      ]}
    />
  )
}
