import InfoPageLayout from '../components/InfoPageLayout'

export default function About() {
  return (
    <InfoPageLayout
      title="Ada Lovelace"
      subtitle="(1818–1852) Mathematikerin, die einen wichtigen Beitrag zur frühen Computertechnik lieferte."
      headerImage="/images/Lovelace_Algorithmus_Auszug_ausg.png"
      backgroundImages={[

  ]}
  headerTexts={[
{
  content: [
    { content: 'Tochter der britischen Aristokratin Anne Isabella Milbanke', indent: -1},
    {content: 'und des romantischen Dichters Lord Byron.', indent: 3},
    {content: 'Den Titel <em><strong>erste Pionierin</em></strong> bekam sie für ihre Übersetzung', indent: 4, spacing: 1},
    {content: 'und weiterführenden Kommentare zu der', indent: 2},
    {content: '<em><strong>Analytical Engine</em></strong> von Charles Babbage.', indent: 5}
  ],
  position: { top: 'top-64', left: 'right-24' },
  className: 'text-lg text-white',
  lineHeight: '1.0'
}
  ]}
      sections={[
                // Nur Text (rechts oben)
        {
          type: 'text',
          align: 'left',
          top: '-10%',
          heading: '',
          marginTop: '-200px',
          width: '70%',
          className: 'not-italic',
          lineHeight: '0.5',
          paragraphs: [
            {content: '„Schon als Kind außergewöhnlich wissbegierig,', indent: 0},
            {content: 'verschreibt sich die Engländerin <strong>Ada Lovelace</strong> der Mathematik.', indent: 2},
            {content: 'Mit ihrem Mentor Charles Babbage forscht die Gräfin ab', indent: 5,  spacing: 2.5},
            {content: '<strong>1833</strong> an einer revolutionären Rechenmaschine.', indent: 6},
            {content: 'Und erkennt, dass ein Gerät dieser Art mittels eines', indent: 4,  spacing: 2.5},
            {content: '<strong>Codes</strong> Informationen verarbeiten könnte:', indent: 5},
            {content: 'Es ist eine Vision des <strong>modernen Computers</strong> – die', indent: 12,  spacing: 2.5},
            {content: 'jedoch kaum einer ihrer Zeitgenossen versteht.“', indent:14}
          ]
        },

        // Nur Bild (zentriert)
         // Nur Bild (zentriert)
        {
        type: 'image',
        src: '/images/Algorithmus_Tabelle.jpg',
        alt: 'Algorithmus Tabelle Bernoulli Zahlen',
        align: 'right',
        marginTop: '-560px',
        size: 'max-w-sm',  // Feste Pixel-Breite
          caption: [
            'Berechnungen für',
            'die Analytical Engine'
          ]
        },
        {
        type: 'image',
        src: '/images/Ada-Lovelace.jpg',
        alt: 'Porträt Ada Lovelace',
        align: 'right',
        marginTop: '-20px',
        size: 'w-64',  // Feste Pixel-Breite
          caption: [
            'Ada Lovelace, 1840,',
            'gemalt von <strong>Margaret Sarah Carpenter</strong>,',
            'einer der erfolgreichsten',
            'Künstlerinnen ihrer Zeit',
            'in Großbritannien'
          ]
        },
        {
        type: 'image',
        src: '/images/Lovelace_Algo_ausg_03.png',
        alt: 'Porträt Ada Lovelace',
        align: 'right',
        marginTop: '-260px',
        size: '60%',  // Feste Pixel-Breite
          caption: [
          ]
        },
        {
        type: 'image',
        src: '/images/Analytical_Engine_ausg.png',
        alt: 'Porträt Ada Lovelace',
        align: 'left',
        marginLeft: '-100px',
        marginTop: '-1200px',
        size: 'max-w-5xl',
        captionAbsolute: true,
        captionLeft: '600px',      // Rechts vom Bild
        captionBottom: '-20px',
        captionWidth: 'w-72',
        caption: [
          'Anayltical Engine von Charles Babbage']
        },

        // Text rechts
        {
          type: 'text',
          align: 'right',
          width: '40%',
          paragraphs: [
            'Ein weiterer Abschnitt...',
            'Mehr Text hier...'
          ]
        },

        // Bild + Text nebeneinander (Bild links)
        {
          type: 'imageText',
          imageRight: false,
          image: {
            src: '/images/algorithm.jpg',
            alt: 'Algorithmus'
          },
          heading: 'Der erste Algorithmus',
          paragraphs: [
            'Ada schrieb 1843...',
            'Dies war der erste...'
          ]
        },

        // Bild + Text nebeneinander (Bild rechts)
        {
          type: 'imageText',
          imageRight: true,
          image: {
            src: '/images/machine.jpg',
            alt: 'Analytical Engine'
          },
          heading: 'Die Analytical Engine',
          paragraphs: [
            'Charles Babbage entwickelte...',
            'Ada erkannte das Potenzial...'
          ]
        }
      ]}
    />
  )
}
