import { useEffect } from 'react';
import InfoPageLayout from '../components/InfoPageLayout'

export default function Lovelace() {
  useEffect(() => {
    window.scrollTo(0, 0);
  }, []);

  return (
    <InfoPageLayout
      title="Ada Lovelace"
      backgroundColor="bg-white"
      backButtonColor="#a855f7"
      subtitle="(1818–1852) Mathematikerin, die einen wichtigen Beitrag zur frühen Computertechnik lieferte"
      headerImage="/images/Lovelace_Algorithmus_Auszug_ausg.png"
      headerImageSize="contain"
      headerImagePosition="center"
      headerImageOpacity="0.6"
      sections={[
        // Intro Text
        {
          type: 'text',
          align: 'right',
          width: '70%',
          marginTop: '-700px',
          marginRight: '10px',
          linneHeiht: '1.3',
          paragraphs: [
            {
              content: 'Tochter der britischen Aristokratin Anne Isabella Milbanke und des romantischen Dichters Lord Byron.'
            }, 
            {
              content: 'Den Titel <em><strong>erste Pionierin</strong></em> bekam sie für ihre Übersetzung und weiterführenden Kommentare zu der <em><strong>Analytical Engine</strong></em> von Charles Babbage.'
            }
          ]
        },

        // Erste Textsektion
        {
          type: 'text',
          align: 'left',
          marginTop: '-200px',
          width: '70%',
          paragraphs: [
            {
              content: 'Schon als Kind außergewöhnlich wissbegierig, verschreibt sich die Engländerin <strong>Ada Lovelace</strong> der Mathematik. Mit ihrem Mentor Charles Babbage forscht die Gräfin ab <strong>1833</strong> an einer revolutionären Rechenmaschine. Und erkennt, dass ein Gerät dieser Art mittels eines <strong>Codes</strong> Informationen verarbeiten könnte: Es ist eine Vision des <strong>modernen Computers</strong> – die jedoch kaum einer ihrer Zeitgenossen versteht.'
            }
          ]
        },

        // Bild: Porträt
        {
          type: 'image',
          srcBW: '/images/Lovelace_Poträt_sw.jpg',
          srcColor: '/images/Ada-Lovelace.jpg',
          alt: 'Porträt Ada Lovelace',
          align: 'right',
          marginTop: '-650px',
          size: 'max-w-xs',
          captionPosition: 'below',
          caption: [
            'Ada Lovelace, 1840,',
            'von <strong>Margaret Sarah Carpenter</strong>,',
            'einer der erfolgreichsten',
            'Künstlerinnen ihrer Zeit',
            'in Großbritannien'
          ]
        },

        // Bild: Tabelle
        {
          type: 'image',
          srcBW: '/images/Lovelace_Tabelle_02_sw.png',
          srcColor: '/images/Lovelace_Tabelle_02.png',
          alt: 'Tabelle',
          align: 'right',
          marginTop: '-200px',
          customWidth: '60%',
          caption: []
        },

        // Bild: Analytical Engine
        {
          type: 'image',
          srcBW: '/images/Lovelace_Analytical_Engine_sw_02.png',
          srcColor: '/images/Lovelace_Analytical_Engine_ausg.png',
          alt: 'Analytical Engine',
          align: 'left',
          marginLeft: '-100px',
          marginTop: '-400px',
          size: 'max-w-4xl',
          captionAbsolute: true,
          captionLeft: '550px',
          captionBottom: '-17px',
          captionWidth: 'w-72',
          captionRotate: '10deg',
          caption: [
            'Analytical Engine von Charles Babbage'
          ]
        },

        // Zweite Textsektion
        {
          type: 'text',
          align: 'right',
          marginTop: '-800px',
          marginRight: '-450px',
          width: '70%',
          paragraphs: [
            {
              content: 'Zudem war Ada Lovelace Musikerin und Dichterin, sie wollte das menschliche Fliegen durch Dampfantrieb revolutionieren, war selbstbewusste Wissenschaftlerin und Förderin männlicher und weiblicher Zeitgenossen und hatte ein interdisziplinäres Wissenschaftverständnis.'
            },
            {
              content: 'Trotz ihrer Weitsicht in den Anmerkungen zur Analytical Engine wurde sie erst in den letzten Jahren als Pionierin der Informatik wiederentdeckt. Auch heute gibt es noch Kritiker, die ihren Beitrag zur Geschichte der Informatik anzweifeln.'
            }
          ]
        },

        // Bild: Algorithmus Tabelle (Original)
        {
          type: 'image',
          src: '/images/Algorithmus_Tabelle.jpg',
          alt: 'Algorithmus Tabelle Bernoulli Zahlen',
          align: 'right',
          marginTop: '-70px',
          marginRight: '-30px',
          size: 'max-w-xl',
          captionAbsolute: true,
          captionLeft: '0',
          captionBottom: '-35px',
          captionWidth: 'w-72',
          caption: [
            'Berechnungen für die Analytical Engine'
          ]
        },

        // Bild: Algorithmus Abstraktion
        {
          type: 'image',
          src: '/images/Lovelace_Algorithmus_04.png',
          alt: 'Abstraktion Algorithmus Tabelle',
          align: 'right',
          marginTop: '-350px',
          marginRight: '-60px',
          size: 'max-w-3xl',
          caption: []
        }
      ]}
    />
  )
}
