    { id: 64, text: ["Harmony"], top: "78%", left: "72.5%", style: "small" },
    { id: 65, text: ["Tempo"], top: "79%", left: "73.5%", style: "big" },
    { id: 66, text: ["Cadence"], top: "75.5%", left: "75%", style: "medium" },
    { id: 67, text: ["Sonata"], top: "80%", left: "71.5%", style: "small" },
    { id: 68, text: ["Opus"], top: "74.5%", left: "73.5%", style: "medium" },

    // Zusätzliche verstreute Texte
    { id: 69, text: ["Wanderer"], top: "25%", left: "35%", style: "big" },
    { id: 70, text: ["Dreamscape"], top: "27%", left: "36%", style: "small" },
    { id: 71, text: ["Whispers"], top: "55%", left: "65%", style: "medium" },
    { id: 72, text: ["Echoes"], top: "57%", left: "66%", style: "small" },
    { id: 73, text: ["Shadows"], top: "30%", left: "60%", style: "big" },
    { id: 74, text: ["Lights"], top: "32%", left: "61%", style: "medium" },
    { id: 75, text: ["Voyage"], top: "60%", left: "40%", style: "small" },
    { id: 76, text: ["Journey"], top: "62%", left: "41%", style: "big" },
    { id: 77, text: ["Destiny"], top: "22%", left: "30%", style: "medium" },
    { id: 78, text: ["Fortune"], top: "24%", left: "31%", style: "small" },
    { id: 79, text: ["Wonder"], top: "82%", left: "45%", style: "big" },
    { id: 80, text: ["Marvel"], top: "84%", left: "46%", style: "medium" },
  ];

  const getTransform = (item) => {
    if (hoveredId === item.id) {
      return 'translate(-50%, -50%) scale(2)';
    }
    if (hoveredId !== null && hoveredId !== item.id) {
      const leftPos = parseFloat(item.left);
      const topPos = parseFloat(item.top);

      const hoveredItem = texts.find(t => t.id === hoveredId);
      const hoveredLeft = parseFloat(hoveredItem.left);
      const hoveredTop = parseFloat(hoveredItem.top);

      const deltaX = leftPos - hoveredLeft;
      const deltaY = topPos - hoveredTop;

      const distance = Math.sqrt(deltaX * deltaX + deltaY * deltaY);
      if (distance < 3) {
        const pushX = deltaX * 17;
        const pushY = deltaY * 15;
        return `translate(${pushX}px, ${pushY}px) scale(0.85)`;
      }
    }
    return 'translate(0, 0) scale(1)';
  };

  return (
    <div className="w-full h-screen overflow-hidden relative">
      {texts.map((item) => (
        <div
          key={item.id}
          className="absolute cursor-pointer"
          style={{ 
            top: item.top, 
            left: item.left,
            zIndex: hoveredId === item.id ? 9999 : 1,
            transform: getTransform(item),
            transition: hoveredId === item.id 
              ? 'transform 1.2s ease-out 0.9s, z-index 0s, top 1.2s ease-out 0.6s, left 1.2s ease-out 0.6s' 
              : 'transform 0.3s ease-out, z-index 0s 0.3s',
            pointerEvents: hoveredId !== null && hoveredId !== item.id ? 'none' : 'auto',
            ...(hoveredId === item.id && {
              top: '50%',
              left: '50%',
            })
          }}
            onMouseEnter={() => handleMouseEnter(item.id)}
            onMouseLeave={handleMouseLeave}
            onClick={() => {
    if (item.link) {
      navigate(item.link);
    }
}}
        >
          <div 
            className={`${styles[item.style].className} tracking-wide`}
          >
            {Array.isArray(item.text) 
              ? item.text.map((line, i) => (
                  <div key={i} style={{ marginTop: i > 0 ? styles[item.style].lineSpacing : '0' }}>
                    {line}
                  </div>
                ))
              : item.text
            }
          </div>
        </div>
      ))}
    </div>
  );
}
