import InfoPageLayout from '../components/InfoPageLayout'

export default function About() {
  return (
    <InfoPageLayout
      title="Ada Lovelace"
      subtitle="(1818-1852) Mathematikerin, die einen wichtigen Beitrag zur frühen Computertechnik lieferte."
      headerImage="/images/Lovelace_Algorithmus_Auszug_ausg.png"
      sections={[
        // Nur Text (links)
        {
          type: 'text',
          align: 'left',
          heading: 'Frühes Leben',
          paragraphs: [
            'Ada wurde 1815 geboren...',
            'Ihre Mutter förderte...',
          ]
        },
          {
    type: 'backgroundSection',
    backgroundImage: '/images/Lovelace-Algo-ausg-02.png',
    height: 'h-[80vh]',  // Höhe anpassbar
    overlay: 0.3,  // Dunkles Overlay für Lesbarkeit (0-1)
    heading: 'Überschrift über dem Bild',
    headingColor: 'text-white',
    content: [
      { type: 'text', content: 'Text über dem Hintergrundbild', className: 'text-white' },
      { type: 'image', src: '/images/small.jpg', size: 'w-64' },
      { type: 'text', content: 'Noch mehr Text' }
    ],
    contentWidth: 'max-w-xl',
    contentAlign: 'left'  // oder 'center', 'right'
  },

        // Nur Bild (zentriert)
        {
          type: 'image',
          src: '/images/Ada-Lovelace.jpg',
          alt: 'Ada Lovelace Portrait',
          align: 'right',
          size: 'w-1/6',
          caption: [
            'Ada Lovelace, 1840,',
            'gemalt von Margaret Sarah Carpenter,',
            'einer der erfolgreichsten',
            'Künstlerinnen ihrer Zeit',
            'in Großbritannien'
          ]
        },

        // Text rechts
        {
          type: 'text',
          align: 'right',
          width: '40%',
          paragraphs: [
            'Ein weiterer Abschnitt...',
            'Mehr Text hier...'
          ]
        },

        // Bild + Text nebeneinander (Bild links)
        {
          type: 'imageText',
          imageRight: false,
          image: {
            src: '/images/algorithm.jpg',
            alt: 'Algorithmus'
          },
          heading: 'Der erste Algorithmus',
          paragraphs: [
            'Ada schrieb 1843...',
            'Dies war der erste...'
          ]
        },

        // Bild + Text nebeneinander (Bild rechts)
        {
          type: 'imageText',
          imageRight: true,
          image: {
            src: '/images/machine.jpg',
            alt: 'Analytical Engine'
          },
          heading: 'Die Analytical Engine',
          paragraphs: [
            'Charles Babbage entwickelte...',
            'Ada erkannte das Potenzial...'
          ]
        }
      ]}
    />
  )
}
