import { Link } from 'react-router-dom'
export default function InfoPageLayout({ 
  title, 
  subtitle = '',
  titlePosition = { top: 'top-12', left: 'left-[140px]' },
  subtitlePosition = { top: 'top-28', right: 'left-[200px]', width: 'max-w-2xl' },
  titleSize = 'text-6xl',
  headerImage,
  headerTexts = [],
  backgroundImages = [],
  sections = [],
  showBackButton = true
}) {
  return (
    <div className="min-h-screen bg-white">
      {/* Header Bereich */}
      <div className="relative h-screen">
              {showBackButton && (
        <Link 
          to="/" 
          className="fixed top-8 left-8 z-50 text-black hover:text-gray-600 transition-colors font-inter text-lg"
        >
          Zur√ºck
        </Link>
      )}
        {/* Header Hintergrundbild (optional) */}
        {headerImage && (
          <img 
            src={headerImage} 
            alt="Header" 
            className="absolute top-0 left-0 w-full h-full object-cover" 
          />
        )}

        {/* Titel links */}
        <h1 className={`absolute ${titlePosition.top} ${titlePosition.left} ${titleSize} font-bold font-inter text-black z-10 max-w-md`}>
          {title}
        </h1>

        {/* Untertitel/Beschreibung rechts */}
        {subtitle && (
         <div className={`absolute ${subtitlePosition.top} ${subtitlePosition.right} ${subtitlePosition.width || 'max-w-md'} z-10`}>
            <p className="text-sm leading-relaxed font-inter">
              {subtitle}
            </p>
          </div>
        )}
      </div>
      {headerTexts.map((text, i) => (
  <div 
    key={i}
    className={`absolute z-10 ${text.position.top || ''} ${text.position.bottom || ''} ${text.position.left || ''} ${text.position.right || ''} ${text.width || 'max-w-md'}`}
  >
    {Array.isArray(text.content) 
      ? text.content.map((line, j) => (
          <p 
            key={j}
            className={`font-inter ${text.className || 'text-lg'} mb-2`}
            style={{ 
              lineHeight: text.lineHeight,
              marginLeft: line.indent ? `${line.indent}rem` : 0
            }}
          >
            {line.content || line}
          </p>
        ))
      : (
          <p 
            className={`font-inter ${text.className || 'text-lg'}`}
            style={{ lineHeight: text.lineHeight }}
          >
            {text.content}
          </p>
        )
    }
  </div>
))}

      {/* Scrollbare Sections */}
      <div className="relative bg-white">
        {backgroundImages.map((bg, i) => (
    <div
      key={`bg-${i}`}
      className="absolute z-0"
      style={{
        backgroundImage: `url('${bg.image}')`,
        top: bg.top,
        left: bg.left,
        width: bg.width,
        height: bg.height,
        backgroundSize: bg.size || 'contain',
        backgroundRepeat: 'no-repeat',
        backgroundPosition: bg.position || 'center',
        pointerEvents: 'none'
      }}
    />
  ))}
        {sections.map((section, index) => {
          if (section.type === 'text') {
            return (
              <div 
                key={index}
                className={`py-20 px-12 ${section.align === 'right' ? 'ml-auto' : ''} ${section.align === 'center' ? 'mx-auto' : ''}`}
                style={{ maxWidth: section.width || '50%' }}
              >
                {section.heading && (
                  <h2 className={`${section.headingSize || 'text-3xl'} font-bold mb-6 font-inter`}>
                    {section.heading}
                  </h2>
                )}
                {section.paragraphs.map((para, i) => (
                  <p 
                    key={i}
                    className={`text-lg font-inter font-italic mb-4 ${para.className || ''} ${section.className || ''}`}
                    style={{ 
                      marginLeft: para.indent ? `${para.indent}rem` : 0,
                      fontSize: para.size,
                      lineHeight: section.lineHeight || para.lineHeight
                    }}
                  >
                    {para.content || para}
                  </p>
                ))}
              </div>
            );
          }

          if (section.type === 'image') {
            return (
              <div 
                key={index}
                className={`py-20 px-12 ${section.align === 'center' ? 'flex justify-center' : ''} ${section.align === 'right' ? 'flex justify-end' : ''}`}
              >
                <img 
                  src={section.src}
                  alt={section.alt || `Bild ${index}`}
                  className={`${section.size || 'w-2/3'} ${section.className || ''}`}
                  style={section.style || {}}
                />
                {section.caption && (
  <div className="text-sm text-black mt-2 font-inter italic">
    {Array.isArray(section.caption) 
      ? section.caption.map((line, i) => (
          <p key={i} className="mb-1">{line}</p>
        ))
      : <p>{section.caption}</p>
    }
  </div>
)}
              </div>
            );
          }

          if (section.type === 'imageText') {
            return (
              <div 
                key={index}
                className={`py-20 px-12 grid ${section.imageRight ? 'grid-cols-[1fr,1.5fr]' : 'grid-cols-[1.5fr,1fr]'} gap-12 items-center`}
              >
                {!section.imageRight && (
                  <img 
                    src={section.image.src}
                    alt={section.image.alt || 'Bild'}
                    className={`w-full ${section.image.className || ''}`}
                  />
                )}
                <div>
                  {section.heading && (
                    <h2 className={`${section.headingSize || 'text-3xl'} font-bold mb-6 font-inter`}>
                      {section.heading}
                    </h2>
                  )}
                  {section.paragraphs.map((para, i) => (
                    <p 
                      key={i}
                      className="text-lg leading-relaxed font-inter mb-4"
                    >
                      {para.content || para}
                    </p>
                  ))}
                </div>
                {section.imageRight && (
                  <img 
                    src={section.image.src}
                    alt={section.image.alt || 'Bild'}
                    className={`w-full ${section.image.className || ''}`}
                  />
                )}
              </div>
            );
          }
          return null;
        })}
      </div>
    </div>
  );
}
