import { Link } from 'react-router-dom'
export default function InfoPageLayout({ 
  title, 
  subtitle = '',
  titlePosition = { top: 'top-12', left: 'left-[140px]' },
  subtitlePosition = { top: 'top-24', right: 'right-[550px]', width: 'max-w-xl' },
  titleSize = 'text-6xl',
  headerImage,
  sections = [],
  showBackButton = true
}) {
  return (
    <div className="min-h-screen bg-white">
      {/* Header Bereich */}
      <div className="relative h-screen">
              {showBackButton && (
        <Link 
          to="/" 
          className="fixed top-8 left-8 z-50 text-black hover:text-gray-600 transition-colors font-inter text-lg"
        >
          Zur√ºck
        </Link>
      )}
        {/* Header Hintergrundbild (optional) */}
        {headerImage && (
          <img 
            src={headerImage} 
            alt="Header" 
            className="absolute top-0 left-0 w-full h-full object-cover" 
          />
        )}

        {/* Titel links */}
        <h1 className={`absolute ${titlePosition.top} ${titlePosition.left} ${titleSize} font-bold font-inter text-black z-10 max-w-md`}>
          {title}
        </h1>

        {/* Untertitel/Beschreibung rechts */}
        {subtitle && (
         <div className={`absolute ${subtitlePosition.top} ${subtitlePosition.right} ${subtitlePosition.width || 'max-w-md'} z-10`}>
            <p className="text-sm leading-relaxed font-inter">
              {subtitle}
            </p>
          </div>
        )}
      </div>

      {/* Scrollbare Sections */}
      <div className="relative bg-white">
        {sections.map((section, index) => {
          if (section.type === 'text') {
            return (
              <div 
                key={index}
                className={`py-20 px-12 ${section.align === 'right' ? 'ml-auto' : ''} ${section.align === 'center' ? 'mx-auto' : ''}`}
                style={{ maxWidth: section.width || '50%' }}
              >
                {section.heading && (
                  <h2 className={`${section.headingSize || 'text-3xl'} font-bold mb-6 font-inter`}>
                    {section.heading}
                  </h2>
                )}
                {section.paragraphs.map((para, i) => (
                  <p 
                    key={i}
                    className={`text-lg leading-relaxed font-inter mb-4 ${para.className || ''}`}
                    style={{ 
                      marginLeft: para.indent ? `${para.indent}rem` : 0,
                      fontSize: para.size
                    }}
                  >
                    {para.content || para}
                  </p>
                ))}
              </div>
            );
          }

          if (section.type === 'image') {
            return (
              <div 
                key={index}
                className={`py-20 px-12 ${section.align === 'center' ? 'flex justify-center' : ''} ${section.align === 'right' ? 'flex justify-end' : ''}`}
              >
                <img 
                  src={section.src}
                  alt={section.alt || `Bild ${index}`}
                  className={`${section.size || 'w-2/3'} ${section.className || ''}`}
                  style={section.style || {}}
                />
                {section.caption && (
                  <p className="text-sm text-gray-600 mt-2 font-inter italic">
                    {section.caption}
                  </p>
                )}
              </div>
            );
          }

          if (section.type === 'imageText') {
            return (
              <div 
                key={index}
                className={`py-20 px-12 grid ${section.imageRight ? 'grid-cols-[1fr,1.5fr]' : 'grid-cols-[1.5fr,1fr]'} gap-12 items-center`}
              >
                {!section.imageRight && (
                  <img 
                    src={section.image.src}
                    alt={section.image.alt || 'Bild'}
                    className={`w-full ${section.image.className || ''}`}
                  />
                )}
                <div>
                  {section.heading && (
                    <h2 className={`${section.headingSize || 'text-3xl'} font-bold mb-6 font-inter`}>
                      {section.heading}
                    </h2>
                  )}
                  {section.paragraphs.map((para, i) => (
                    <p 
                      key={i}
                      className="text-lg leading-relaxed font-inter mb-4"
                    >
                      {para.content || para}
                    </p>
                  ))}
                </div>
                {section.imageRight && (
                  <img 
                    src={section.image.src}
                    alt={section.image.alt || 'Bild'}
                    className={`w-full ${section.image.className || ''}`}
                  />
                )}
              </div>
            );
          }

          return null;
        })}
      </div>
    </div>
  );
}
